import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "../components/Header";
import { useNavigate, useLocation, Link } from 'react-router-dom';
import { BASE_URL } from '../fetchApi';
import { io } from "socket.io-client";

const Quiz1v1 = () => {
    const base_url = BASE_URL;
    const navigate = useNavigate();
    const location = useLocation();
    const data = location.state;
    const socket = io(base_url);

    const [msg, setMsg] = useState("");
    const [gameId, setGameId] = useState("");
    const [userId, setUserId] = useState("");

    useEffect(() => {
        const token = sessionStorage.getItem('token');
        if (!token) {
            navigate('/login');
        }

        setMsg("Joining game, please wait...");
        setUserId(sessionStorage.getItem('user_id'));

        matchmaking();
    }, [navigate]);

    useEffect(() => {
        socket.on("connect", () => {
            console.log("Connected to server");
        });

        socket.on("playerJoined", (data) => {
            console.log(data);
            setMsg(data.msg);
            navigate(`/quizpage`, { state: data });
        });

        socket.on("computerJoined", (data) => {
            console.log(data);
            setMsg(data.msg);
            navigate(`/quizpage`, { state: data });
        });

        socket.on("disconnect", () => {
            console.log("Disconnected from server");
        });

        return () => {
            socket.off("connect");
            socket.off("playerJoined");
            socket.off("computerJoined");
            socket.off("disconnect");
        };
    },[]);

    const matchmaking = async () => {
        try {
            const response = await fetch(`${base_url}/matchmaking`, {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ 
                    userId: sessionStorage.getItem('user_id')
                })
            });

            if (!response.ok) {
                throw new Error(`Error: ${response.statusText}`);
            }

            const result = await response.json();
            //setGameId(result.gameId);
            if(result.msg == "Spiel gefunden!"){
                //navigate('/waitinggame',{state: result});
                console.log(result);
                setMsg(result.msg);
                setGameId(result.gameId);
                socket.emit("joinGame", { 
                    gameId: result.gameId,
                    username: sessionStorage.getItem('email'),
                    mode: result.mode,
                    timer:result.timer
                });
            }else{
                setMsg("Please try again later.");
            }
        } catch (error) {
            console.error("Error fetching game data:", error);
        }
    };

    return (
        <div className="container-fluid d-flex flex-column min-vh-100 px-0">
            <Header />
            <main className="d-flex flex-column align-items-center flex-grow-1 mt-5">
                <div className="card p-4 shadow-sm" style={{ width: "95%" }}>
                    <div className="card-header bg-light">Joined Game</div>
                    <div className="card-body">
                        <p>{ msg }</p>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default Quiz1v1;
