import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "../components/Header";
import { useNavigate, useLocation, data } from 'react-router-dom';
import { BASE_URL } from '../fetchApi';
import { io } from "socket.io-client";

const QuizPage = () => {
  const base_url = BASE_URL;
  const location = useLocation();
  const state = location.state;
  const socket = io(base_url);

  const [msg,setMsg] = useState("");
  const [mode,setMode] = useState(state.mode);
  const [timerDuration, setTimerDuration] = useState("");
  const [timeLeft, setTimeLeft] = useState(state.timerDuration);
  const [loopQuestion,setLoopQuestion] = useState([]);
  const [gameId,setGameId] = useState(state.gameId);

  const [question, setQuestion] = useState({});
  const navigate = useNavigate();

  const [quizId,setQuizId] = useState("");

  const [point,setPoint] = useState("");

  const quizStart = async () => {
    try {
      const response = await fetch(`${base_url}/quiz/start`, {
        method: "POST",
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          mode: mode,
          user_id: sessionStorage.getItem('user_id'),
          timerDuration: timerDuration
        }),
      });

      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }

      const result = await response.json();
      setQuizId(result.quizId);
      setLoopQuestion(result.questions);
    } catch (error) {
      console.error("Error fetching quiz data:", error);
    }
  };

  useEffect(() => {
    const token = sessionStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }

    console.log(state);

    setMode(state.mode);
    setTimerDuration(state.timerDuration);
    setGameId(state.gameId);

    console.log(state);

    quizStart();
  }, [navigate]);

  useEffect(() => {
    socket.on("connect", () => {
      console.log("Connected to server");
    });

    socket.on("updateScore", (data) => {
      console.log(data);
      //navigate(`/quizende`, { state: data });
    });

    socket.on("gameOver", (data) => {
      console.log(data);
      //navigate(`/quizende`, { state: data });
    });

    socket.on("error", (data) => {
      console.log(data);
    });

    socket.on("disconnect", () => {
      console.log("Disconnected from server");
    });

    return () => {
      socket.off("connect");
      socket.off("updateScore");
      socket.off("gameOver");
      socket.off("error");
      socket.off("disconnect");
    };
  }, []);

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    }else{
      submitQuiz();
    }
  }, [timeLeft]);



  const handleOptionChange = (questionId, value) => {
    setQuestion((prev) => ({
      ...prev,
      [questionId]: value,
    }));
  };

  const submitQuiz = async () => {
    try {
      // var aTrue = 0;
      var uScore = 0;
      for (const item of loopQuestion) {
        const dataPost = {
          quizId: quizId,
          questionId: item.id,
          answer: question[item.id] || null, // Send the selected answer or null if not answered
          userId: sessionStorage.getItem('user_id')
        };

        //console.log(dataPost);
        const response = await fetch(`${base_url}/quiz/submit`, {
          method: "POST",
          headers: {
          'Content-Type': 'application/json'
          },
          body: JSON.stringify(dataPost)
        });

        if (!response.ok) {
          console.log(`Error submitting question ${item.id}: ` + response.statusText);
          alert(`Failed to submit answer for question ${item.id}`);
          return;
        }

        const result = await response.json();
        // const dataEmit = {
        //   gameId: gameId,
        //   userId: sessionStorage.getItem('user_id'),
        //   questionId: item.id,
        //   answer: question[item.id] || null
        // };
        //console.log(result);
        if(result.points > 0){
          uScore += result.points;
        }

        // navigate('/quizende',{ state:result });
        // socket.emit("submitAnswer",{
        //   gameId: gameId,
        //   userId: sessionStorage.getItem('user_id'),
        //   questionId: item.id,
        //   answer: question[item.id] || null
        // });

        // socket.emit("endGame",{
        //   gameId: gameId
        // });

        //console.log(result);
        // if(result.points > 0){
        //   aTrue++;
        //   uScore += result.points;
        // }

      }

      navigate("/quizende", { state: {gameId:gameId,points:uScore} });
    } catch (error) {
      console.log(error);
      alert("An error occurred while submitting the answers");
    }
  };

  return (
    <div className="container-fluid d-flex flex-column min-vh-100 px-0">
      <Header />
      <main className="d-flex flex-column align-items-center flex-grow-1 mt-5">
        <div className="d-flex justify-content-end w-50 mb-2">
          <span className="badge bg-danger fs-5">{timeLeft} Sekunden, {mode}</span>
        </div>

        {(() => {
          const elements = [];
          for (let index = 0; index < loopQuestion.length; index++) {
            const item = loopQuestion[index];
            elements.push(
              <div key={item.id} className="card p-4 shadow-sm mb-4" style={{ width: "50%" }}>
              <div className="card-header bg-light">Frage {index + 1}</div>
              <div className="card-body">
                <p>{item.question}</p>
                <form>
                {(() => {
                  const options = [];
                  const parsedOptions = Array.isArray(item.options)
                  ? item.options
                  : JSON.parse(item.options || "[]"); // Parse options if it's a string
                  for (let optIndex = 0; optIndex < parsedOptions.length; optIndex++) {
                  const option = parsedOptions[optIndex];
                  options.push(
                    <div className="form-check" key={optIndex}>
                    <input
                      className="form-check-input"
                      type="radio"
                      name={`question-${item.id}`}
                      id={`option-${item.id}-${optIndex}`}
                      value={option}
                      onChange={(e) => handleOptionChange(item.id, e.target.value)}
                    />
                    <label
                      className="form-check-label"
                      htmlFor={`option-${item.id}-${optIndex}`}
                    >
                      {option}
                    </label>
                    </div>
                  );
                  }
                  return options;
                })()}
                </form>
              </div>
              </div>
            );
          }
          return elements;
        })()}

        <div className="d-flex text-center mt-3">
          <button className="btn btn-primary" onClick={submitQuiz}>Antwort best√§tigen</button>
        </div>
      </main>
    </div>
  );
};

export default QuizPage;
