import React from "react";
import Header from "./Header";
import { useNavigate,useLocation } from "react-router-dom";

const QuizEnde = () => {
  const location = useLocation();
  const data = location.state;
  const navigate = useNavigate();

  const msg = data.msg;
  const points = data.points;

  const modus = data.modus;
  const spielerErgebnis = points;
  const gegnerErgebnis = points;

  // Bestimme Nachricht f端r PvP-Modus
  let pvpErgebnisText = "";
  if (modus === "1v1") {
    if (spielerErgebnis > gegnerErgebnis) {
      pvpErgebnisText = "Herzlichen Gl端ckwunsch, du hast gewonnen!";
    } else if (spielerErgebnis < gegnerErgebnis) {
      pvpErgebnisText = "Leider hast du verloren, aber gut gemacht!";
    } else {
      pvpErgebnisText = "Es ist unentschieden! Gut gemacht!";
    }
  }

  const handleBack = () => {
    navigate("/quizstart");
  };

  const handleLeaderboard = () => {
    navigate("/leaderboard");
  };

  return (
    <div className="flex flex-col min-h-screen">
      <Header />
      <div className="flex-grow flex flex-col items-center justify-center bg-gray-100">
        <div className="bg-white p-6 rounded-2xl  w-1/2 text-center">
            <br />
            <br />
            <br />
          <h2 className="text-2xl font-bold" >Quiz beendet! {msg}</h2>

          {modus === "computer" ? (
            <p className="mt-4">Du hast {spielerErgebnis} von 10 Fragen richtig beantwortet!</p>
          ) : (
            <p className="mt-4">
                <br />
                <br />
              Du hast {spielerErgebnis}/10 Fragen richtig beantwortet.
              <br />
            <p> Dein Gegner hat {gegnerErgebnis}/10 Fragen richtig beantwortet. </p>
            </p>
          )}

          {modus === "1v1" && <p className="mt-4 font-bold">{pvpErgebnisText}</p>}

          {/* Buttons (ohne Funktion) */}
          <div className=" mt-6 flex justify-center space-x-4" style={{marginTop: '250px'}}>
            <button className="btn btn-primary me-5" onClick={handleBack}>
              Neues Spiel starten
            </button>
            <button className="btn btn-primary" onClick={handleLeaderboard}>
              Leaderboard sehen
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuizEnde;
