import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "../components/Header";
import { useNavigate } from 'react-router-dom';

const QuizStart = () => {
    const [opponent, setOpponent] = useState("");
    const [time, setTime] = useState(0);

    const navigate = useNavigate();
    const handleStartQuiz = () => {
        if (!opponent || time <= 0) {
            alert("Please select an opponent and set a valid time.");
            return;
        }
        navigate("/quizpage", { state: { opponent, time } });
    };

    return (
    <div className="container-fluid d-flex flex-column min-vh-100 px-0">
        {/* Header: Vollbreit + Fixes Layout */}
        <Header />
        
        {/* Hauptinhalt */}
        
        <main className="d-flex flex-column align-items-center flex-grow-1 mt-5">
        {/* Timer rechts oberhalb des Quiz-Kastens */}
        <div className="d-flex justify-content-end w-50 mb-2">
            <span className="badge bg-info fs-5">Welcome, to start quiz fill the form below!</span>
        </div>

        <div className="card p-4 shadow-sm" style={{ width: "50%" }}>
            <div className="card-header bg-light">Quiz Start</div>
            <div className="card-body">
                <p>Choose your opponents?</p>
                <form>
                    <div className="form-check">
                        <input 
                            className="form-check-input" 
                            type="radio" 
                            name="opponent" 
                            id="opponent1" 
                            value={"VS Computer"}
                            onChange={(e) => setOpponent(e.target.value)} 
                        />
                        <label className="form-check-label" htmlFor="opponent1">VS Computer</label>
                    </div>
                    <div className="form-check">
                        <input 
                            className="form-check-input" 
                            type="radio" 
                            name="opponent" 
                            id="opponent2"
                            value={"1 VS 1"} 
                            onChange={(e) => setOpponent(e.target.value)} 
                        />
                        <label className="form-check-label" htmlFor="opponent2">1 VS 1</label>
                    </div>
                </form>
                <br></br>
                <p>Fill the time</p>
                <form>
                    <div className="form-check">
                        <input className="form-control" type="number" name="time" id="time" min={1} onChange={(e) => setTime(e.target.value)} />
                    </div>
                </form>
            </div>
        </div>
        
        {/* Navigationslinks und Button außerhalb des Frage-Kastens */}
        <div className="d-flex justify-content-between w-50 mt-3">
            <div>&nbsp;</div>
            <button className="btn btn-primary" onClick={handleStartQuiz}>Start</button>
        </div>
        </main>
    </div>
    );
};

export default QuizStart;
