const BASE_URL = 'https://bursting-heroic-akita.ngrok-free.app'; // Ganti dengan URL API Anda

export const fetchApi = async (endpoint, method = 'GET', headers = "", body = "") => {
    try {
        const opt = {
            method: String(method).toUpperCase()
        };

        if(headers != "") {
            opt.headers = headers;
        }

        if (body != "") {
            opt.body = JSON.stringify(body); // Serialize body ke JSON
        }

        const response = await fetch(`${BASE_URL}${endpoint}`, opt);
        
        console.log(response);

        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }

        return response;
    } catch (error) {
        console.error('Fetch API error:', error);
        throw error;
    }
};
