import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "../components/Header";
import { useNavigate, useLocation, Link } from 'react-router-dom';
import { BASE_URL } from '../fetchApi';
import { io } from "socket.io-client";

const WaitingGame = () => {
    const base_url = BASE_URL;
    const navigate = useNavigate();
    const location = useLocation();
    const state = location.state;
    const socket = io(base_url);

    const [msg, setMsg] = useState("");

    useEffect(() => {
        const token = sessionStorage.getItem('token');
        if (!token) {
            navigate('/login');
        }

        setMsg(state.msg);

        socket.emit("joinGame", {
            username: sessionStorage.getItem('email'),
            gameId: state.gameId,
            mode: state.mode,
            timer: state.timerDuration
        });
    }, [navigate]);

    useEffect(() => {
        socket.on("connect", () => {
            console.log("Connected to server");
        });

        socket.on("playerJoined", (data) => {
            console.log(data);
            setMsg(data.msg);
            navigate(`/quizpage`, { state: data });
        });

        socket.on("computerJoined", (data) => {
            console.log(data);
            setMsg(data.msg);
            navigate(`/quizpage`, { state: data });
        });

        socket.on("disconnect", () => {
            console.log("Disconnected from server");
        });
    }, []);

    return (
        <div className="container-fluid d-flex flex-column min-vh-100 px-0">
            <Header />
            <main className="d-flex flex-column align-items-center flex-grow-1 mt-5">
                <div className="card p-4 shadow-sm" style={{ width: "95%" }}>
                    <div className="card-header bg-light">Waiting for Player</div>
                    <div className="card-body">
                        <p>{ msg }</p>
                        {/* <Link to="/quizlobby" className="btn btn-primary">Back to Home</Link> */}
                    </div>
                </div>
            </main>
        </div>
    );

}

export default WaitingGame;
